<script>
import "intersection-observer";
import Scrollama from "../../vue-scrollama/src/Scrollama.vue";
import * as d3 from "d3";

const brainFill1 = d3.selectAll(".cls-2");
const brainFill2 = d3.selectAll(".cls-3");

export default {
  name: "App",
  components: {
    Scrollama,
  },
  data() {
    return {
      currStep: null,
    };
  },
  methods: {
    brainHandler({ element, index, direction }) {
      if (direction === "down") element.classList.add("active");
      // console.log(index);
      if (index === 0) this.phase0();
      if (index === 1) this.phase0();
      if (index === 2) this.phase2();
      if (index === 3) this.phase3();
      if (index === 4) this.phase4();
      if (index === 5) this.phase5();
    },

    brainStart() {
      d3.select("#front-lobe").attr("opacity", 0);
      d3.select("#LC-arrows-3").attr("opacity", 0);
      d3.select("#LC-center").attr("opacity", 0);
      d3.select("#front-lobe-arrows-2").attr("opacity", 0);
      d3.select("#VTA-2").attr("opacity", 0);
      d3.select("#brain-base").attr("opacity", 0.85);
    },

    phase0() {
      d3.select("#front-lobe").attr("opacity", 0);
      d3.select("#LC-arrows-3").attr("opacity", 0);
      d3.select("#LC-center").attr("opacity", 0);
      d3.select("#VTA-2").attr("opacity", 0);
      d3.select("#front-lobe-arrows-2").attr("opacity", 0);
      d3.select("#brain-base")
        .transition()
        .ease(d3.easeLinear)
        .duration(2000)
        .attr("opacity", 0.85);
      brainFill1.style("fill", "#f6adcd");
    },

    phase2() {
      d3.select("#brain-base")
        .transition()
        .ease(d3.easeLinear)
        .duration(2000)
        .attr("opacity", 0.4);
      d3.select("#front-lobe").attr("opacity", 0.5);
      d3.select("#LC-arrows-3").attr("opacity", 0);
      d3.select("#VTA-2").attr("opacity", 0.4);
      d3.select("#front-lobe-arrows-2").attr("opacity", 1);
      const arrowLineDrawn = d3.selectAll(".cls-5").attr("opacity", 1);
      const arrowDrawn = d3.selectAll(".cls-6").attr("opacity", 0);
      arrowLineDrawn
        .attr("stroke-dasharray", function () {
          return this.getTotalLength();
        })
        .attr("stroke-dashoffset", function () {
          return this.getTotalLength();
        })
        // reveal the paths
        .transition()
        .duration(4000)
        .attr("stroke-dashoffset", 0);
      arrowDrawn
        // reveal the arrowheads
        .transition()
        .duration(6000)
        .attr("opacity", 1)
        .attr("transform", function () {
          return "translate(" + this.x + "," + this.y + ")";
        });

      d3.select("#LC-center").attr("opacity", 1);
    },

    phase3() {
      d3.select("#brain-base")
        .transition()
        .ease(d3.easeLinear)
        .duration(2000)
        .attr("opacity", 1);
      brainFill2
        .transition()
        .ease(d3.easeLinear)
        .duration(4000)
        .style("fill", "#f17fb1")
        .attr("opacity", 1);
      brainFill1
        .transition()
        .ease(d3.easeLinear)
        .duration(4000)
        .style("fill", "#f17fb1")
        .attr("opacity", 1);
      d3.select("#front-lobe").attr("opacity", 0);
      d3.select("#LC-arrows-3").attr("opacity", 1);
      d3.select("#VTA-2").attr("opacity", 0);
      d3.select("#front-lobe-arrows-2").attr("opacity", 0);
      const arrowLineDrawn = d3.selectAll(".cls-5").attr("opacity", 1);
      const arrowDrawn = d3.selectAll(".cls-6").attr("opacity", 0);
      arrowLineDrawn
        .attr("stroke-dasharray", function () {
          return this.getTotalLength();
        })
        .attr("stroke-dashoffset", function () {
          return this.getTotalLength();
        })
        // reveal the paths
        .transition()
        .duration(4000)
        .attr("stroke-dashoffset", 0);
      arrowDrawn
        // reveal the arrowheads
        .transition()
        .duration(6000)
        .attr("opacity", 1)
        .attr("transform", function () {
          return "translate(" + this.x + "," + this.y + ")";
        });
    },
    phase4() {
      const arrowLineDrawn = d3.selectAll(".cls-5").attr("opacity", 1);
      const arrowDrawn = d3.selectAll(".cls-6");
      arrowLineDrawn
        .transition()
        .delay(0)
        .on("start", function repeat() {
          d3.active(this)
            .duration(1000)
            .attr("stroke-dasharray", function () {
              return this.getTotalLength();
            })
            .attr("stroke-dashoffset", function () {
              return this.getTotalLength();
            })
            .transition()
            .delay(200)
            .duration(1000)
            .attr("stroke-dasharray", function () {
              return this.getTotalLength();
            })
            .attr("stroke-dashoffset", function () {
              return this.getTotalLength();
            })
            // reveal the paths
            .transition()
            .duration(1000)
            .attr("stroke-dashoffset", 0)
            .transition()
            .delay(200)
            .duration(1000)
            .attr("stroke-dasharray", function () {
              return this.getTotalLength();
            })
            .attr("stroke-dashoffset", function () {
              return this.getTotalLength();
            })
            .transition()
            .delay(200)
            .duration(1000)
            .attr("stroke-dasharray", function () {
              return this.getTotalLength();
            })
            .attr("stroke-dashoffset", function () {
              return this.getTotalLength();
            })
            // reveal the paths
            .transition()
            .duration(2000)
            .attr("stroke-dashoffset", 0)
            .on("start", repeat);
        });

      arrowDrawn
        .attr("transform", function () {
          return "translate(" + this.x + "," + this.y + ")";
        })
        .transition()
        .delay(350)
        .on("start", function repeat() {
          d3.active(this)
            .attr("opacity", 0)
            .transition()
            .delay(350)
            .duration(2500)
            .attr("opacity", 1)
            .transition()
            .delay(350)
            .duration(2500)
            .attr("opacity", 0)
            .transition()
            .delay(350)
            .duration(2500)
            .attr("opacity", 1)
            .transition()
            .delay(350)
            .on("start", repeat);
        });

      d3.select("#brain-base")
        .transition()
        .delay(350)
        .on("start", function repeat() {
          d3.active(this)
            .attr("opacity", 0.5)
            .transition()
            .delay(350)
            .duration(2500)
            .attr("opacity", 1)
            .transition()
            .delay(350)
            .duration(2500)
            .attr("opacity", 0.5)
            .transition()
            .delay(350)
            .duration(2500)
            .attr("opacity", 1)
            .transition()
            .delay(350)
            .on("start", repeat);
        });

      d3.select("#front-lobe").attr("opacity", 0);
      d3.select("#LC-arrows-3").attr("opacity", 1);
      d3.select("#VTA-2").attr("opacity", 0);
      d3.select("#front-lobe-arrows-2").attr("opacity", 0);

      //animate arrows
    },

    phase5() {
      d3.select("#brain-base")
        .transition()
        .ease(d3.easeLinear)
        .duration(2000)
        .attr("opacity", 0.7);

      //animate arrows
      const arrowLineDrawn = d3.selectAll(".cls-5").attr("opacity", 1);
      const arrowDrawn = d3.selectAll(".cls-6");
      arrowLineDrawn
        .transition()
        .duration(4000)
        .attr("stroke-dasharray", function () {
          return this.getTotalLength();
        })
        .attr("stroke-dashoffset", function () {
          return this.getTotalLength();
        });
      arrowDrawn
        .transition()
        .duration(4000)
        .attr("transform", "translate(0,200)")
        .attr("opacity", 0);
    },
  },
  mounted() {
    this.brainStart();
  },
};
</script>

